  `status` varchar(15) NOT NULL,
  `author` varchar(55) NOT NULL,
  `genre` varchar(126) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

--
-- Дамп данных таблицы `anime_serials`
--

INSERT INTO `anime_serials` (`id`, `name`, `year`, `rating`, `description`, `picture`, `status`, `author`, `genre`) VALUES
(1, 'Trigun/Триган', 1998, '8.23', '«Trigun» — известный в своём жанре космического вестерна сериал, повествующий о парне по имени Вэш Ураган и его похождениях с двумя девушками из страхового агентства «Бернарделли». Сотрудницы агентства всюду следуют за Вэшем, пытаясь свести к минимуму те разрушения, которые неизбежно появляются вслед за приходом Вэша куда бы то ни было.\r\nВсё дело в охотниках за головами, жаждущих получить награду в «$$ 60,000,000,000» (шестьдесят миллиардов «двойных долларов»), которая назначена за голову Вэша за то, что тот единолично уничтожил город Июль. Хотя сам Вэш не может вспомнить этого инцидента из-за случившейся с ним амнезии.\r\nНа протяжении всего путешествия Вэш пытается спасти свою жизнь «не смертоносными» методами. И время от времени ему составляет компанию священник Николас Дэ Вульфвуд, который, как и Вэш, является превосходным стрелком с загадочным прошлым.', '\\js\\title\\serial\\picture\\trigun\\unnamed.jpg', 'Завершен', 'Madhouse studio', 'Экшен Фантастика Приключение Комедия Драма Сенён'),
(2, 'Huter x Hunter', 1999, '8.41', 'Охотниками в этом мире зовут людей, работающих на правительство и путешествующих по миру, выполняя всевозможные опасные задачи, начиная с поимки преступников и заканчивая поиском сокровищ в неизведанных землях.\r\nЗвание Охотника налагает на владельца значительное число обязанностей и ограничений, однако даёт и множество привилегий. Многие стремятся стать Охотниками из корыстных побуждений, но лишь немногие добиваются этого: испытание на звание длинно, сурово и смертельно опасно.\r\nГлавный герой серии – юноша Гон, чей отец, будучи знаменитым Охотником, пропадает без вести много лет назад. Когда Гону исполняется двенадцать, он, задавшись целью стать Охотником и найти отца, покидает родной дом и принимает участие в Испытании.', '\\js\\title\\serial\\picture\\hunter_x_hunter\\hunter_x_hunter_1\\136.jpg', 'выпускается', 'Nippon Animation', 'Экшен Фантастика Приключение Сенён Фэнтези'),
(3, 'Berserk', 1997, '8.49', 'Отважный молодой воин Гатс испытывает судьбу в качестве наёмника на передовой. Роковая встреча сводит Гатса с харизматичным и беспощадным юношей по имени Гриффит. Возглавляя непобедимый отряд, известный как «Банда Ястреба», Гриффит владеет грозной силой, несравнимой ни с чем. Движимые стремлением к статусу, «ястребы» проявляют невиданную доселе доблесть в военных делах и выдержку в делах политических. Армии Мидланда содрогнутся в страхе, когда Гриффит и Гатс вступят на путь, на котором им суждено поставить мир на колени.', '\\js\\title\\serial\\picture\\berserk\\berserk.jpg', 'Продолжается ', 'OLM', 'Экшен Приключения Демоны Драма Фэнтези Ужасы Военное'),
(4, 'Ковбой Бибоп / Cowboy Bebop', 1998, '8.78', '2071 год. Человечество колонизировало всю Солнечную Систему, основав колонии от Венеры до Юпитера. Но десятилетия тому назад из-за техногенной катастрофы была уничтожена Луна. Последствия оказались катастрофическими: непрерывные метеоритные дожди сделали жизнь на поверхности Земли невозможной, а в первые недели после катастрофы погибло 4,7 миллиарда человек. Большая часть выживших перебралась в колонии на другие планеты.\r\nСо временем по всей Солнечной Системе разрослись и набрали силу преступные синдикаты, для борьбы с которыми правительство возродило древнюю практику охоты за головами. Отныне охотники за головами разъезжают по всей Солнечной Системе в поисках целей.\r\nСпайк Шпигель и Джет Блэк — охотники. Волею судьбы они оказались на космическом корабле «Bebop 268710». Путешествуя вместе, они подбирают Фэй Валентайн — очаровательную картёжницу с невероятно огромным долгом, Радикал Эдварда — компьютерного гения и генетически модифицированную собаку Эйн.\r\nНа борту «Bebop» судьба сводит четырёх человек и одну собаку, и так начинаются их совместные приключения...', '\\js\\title\\serial\\picture\\cowboy_bebop\\cowboy_bebop.jpg', 'Завершен', 'Sunrise', 'Экшен,Приключения,Комедия,Драма,Фантастика,Космос'),
(5, 'Наруто / Naruto', 2002, '7.92', 'В день рождения Наруто Узумаки на деревню под названием Коноха напал легендарный демон, Девятихвостый лис. Четвёртый Хокагэ ценой своей жизни спас деревню, запечатав демона в новорождённом Наруто, неосознанно обрекая его на жизнь в ненависти односельчан.\r\nНесмотря на недостаток таланта во многих областях ниндзюцу, неусидчивость и задиристость, у Наруто есть мечта — стать Хокагэ, сильнейшим ниндзя в деревне. Желая признания, которого не получал, он упорно работает и тренируется вместе со своими напарниками, Саскэ Учихой и Сакурой Харуно, а также со своим наставником Какаши Хатакэ. Ему и его напарникам придётся пройти через многое по пути к своим заветным мечтам: сражения, любовь, дружба, предательство, жажда силы...', '\\js\\title\\serial\\picture\\naruto\\naruto.jpg', 'Завершен', 'Pierot', 'Экшен,Приключения,Комедия,Супер сила,Боевые искусства,Сёнен');

